<?php/***	@desc View economy*	- Resources stats*	- Bourse*	@author romain dumont*/	class Veconomy{		private $_oLoad;		private $_oSmarty;		private $_oSession;		private $_oLanguage;		function __construct(){		  $this->_oLoad = new Loader();		  //session		  $this->_oSession = $this->_oLoad->load('Session');		  //language		  $this->_oLanguage = $this->_oLoad->load('Language',array('sPageName'=>'economy'));		  //smarty		  $this->_oSmarty = $this->_oLoad->load('Smartyext');		  //Data build		  $this->_oDeconomy = $this->_oLoad->load('Deconomy');		  //load the view		  $this->_loadView();		}		private function _loadView(){						$slang = $this->_oSession->getFromSession('lang');			//economy stats			$this->_oSmarty->assign('aResourcesStats',$this->_oDeconomy->getResourcesStats(false,false));			$this->_oSmarty->assign('food',$this->_oLanguage->food->$slang);			$this->_oSmarty->assign('wood',$this->_oLanguage->wood->$slang);			$this->_oSmarty->assign('iron',$this->_oLanguage->iron->$slang);			$this->_oSmarty->assign('stone',$this->_oLanguage->stone->$slang);			$this->_oSmarty->assign('gold',$this->_oLanguage->gold->$slang);			$this->_oSmarty->assign('libellecity',$this->_oLanguage->libellecity->$slang);			//--					}				public function displayView(){		  $this->_oSmarty->display('economy.tpl');		}	}?>